        justifyContent: "space-between"
      }}
    >
      <h1>Home</h1>
      <div id="user" />
    </div>
    <div id="nav">
      <div id="el" style={{ color: "#000" }}>
        Top picks
      </div>
      <div id="el">Recent</div>
      <div id="el">Collections</div>
      <div id="el">Explore</div>
    </div>
  </>
);

function App() {
  const [{ x, y }, set] = useState(() => ({ x: 0, y: heightOffset }));
  const [index, setIndex] = useState(0);
  const [active, setActive] = useState(false);
  const bind = useDrag(
    ({ down, movement: [x, y], distance, direction: [xDir], velocity }) => {
      const trigger = velocity > 0.2;
      const dir = xDir < 0 ? -1 : 1;
      if (!active) {
        if (trigger && !down) {
          if (!(index + dir * -1 >= imgData.length || index + dir * -1 < 0)) {
            setIndex(index + dir * -1);
          }
          set({ x: 0, y: heightOffset });
        } else {
          set({ x: down ? x : 0, y: heightOffset });
        }
      }
    }
  );

  return (
    <div id="app" {...bind()}>
      {/* {Head} */}
      {imgData.map((data, i) => {
        return (
          <Card
            key={i}
            _x={x}
            _y={y}
            num={i}
            index={index}
            data={data}
            active={active}
            setActive={setActive}
          />
        );
      })}
      <Info active={active} index={index} setActive={setActive} />
    </div>
  );
}

export default App;
